workspace.query:
xquery version "1.0-ml";

for $i in (1 to 10000)
return 
  xdmp:document-insert(
    "/" || $i || ".xml",
    <doc>
      {
        let $lo := xdmp:random(10)
        let $hi := $lo + xdmp:random(10)
        return (
          <lo>{$lo}</lo>,
          <hi>{$hi}</hi>
        )
      }
      <id>{$i}</id>
    </doc>
  )
